@Component({
  selector: 'app-movie-card',
  templateUrl: './movie-card.component.html',
  styleUrls: ['./movie-card.component.css'],
})
export class MovieCardComponent {
  @In
}

<div class="movie-card">
  <img
    class="poster"
    src="https://www.cnc.fr/documents/36995/83669/FE_Jeune_Juliette_vgtte.jpg/1e40cc94-046c-e25d-b9ab-bcf0161e55b5?t=1657542201450"
    alt="Movie Poster"
  />
  <div class="movie-details">
    <h2 class="title">Jeune Juliette</h2>
    <p class="release-date">Release date: June 8, 2018</p>
  </div>
</div>
